import {useSelector, useDispatch} from 'react-redux'
import { useEffect } from 'react'
import { selectAllPosts, getPostsError, getPostsStatus, fetchPosts, deletePost } from '../../store'
import './lista.css'

export default function Lista(props){
    const dispatch = useDispatch()

    const posts = useSelector(selectAllPosts)
    const postsStatus = useSelector(getPostsStatus)
    const postsError = useSelector(getPostsError)

    const filtro = useSelector(state => state.filter.filter)

    console.log(filtro)
    useEffect(()=>{
        if (postsStatus === 'idle'){
            dispatch(fetchPosts())
        }
    }, [postsStatus, dispatch])

    const handleDelete = (id) =>{
        dispatch(deletePost(id))
    }

    return(
        <div className="Lista">
            <table>
                <tr>
                    <th>Nombre</th>
                    <th>Descripción</th>
                    <th>Acción</th>
                </tr>
                {posts.map(post => (
                    <tr>
                        <td>{post?.nombre}</td>
                        <td>{post?.descripcion}</td>
                        <td><button className="deleteButton" id={'deleteButton_'+post?.id} onClick={()=>handleDelete(post?.id)}><span className='whiteFont'>Borrar</span></button></td>
                    </tr>
                ))}
                </table>
        </div>
    )

}
