import './formulario.css'
import Boton from '../Boton/Boton'
import { createPost } from '../../store'
import { useState } from 'react'
import { useDispatch } from 'react-redux'

export default function Formulario(){
    const dispatch = useDispatch()
    const [postData, setPostData] = useState([])
    const handleSubmit = (e) => {
        e.preventDefault()
        console.log(postData.nombre, postData.description)
        if(postData.nombre !== '' && postData.description !== ''){
            dispatch(createPost(postData))
            setPostData({ title: "", description: "", tags: [] });
        }else{
            alert('Falta información')
        }
    }

    const onInputChange = (e) =>{
        const {name, value} = e.target;
        setPostData({... postData, [name]: value})
    }
    return(
        <form className="Form" onSubmit={handleSubmit}>
            <input type="text" name='nombre' placeholder="Nombre" onChange={onInputChange}/>
            <input type="text" name='descripcion' placeholder="Descripción" onChange={onInputChange}/>
            <button>Crear</button>
        </form>
    )
}
