import './filtro.css'
import Boton from '../Boton/Boton'
import { useSelector, useDispatch } from 'react-redux'
import { setFilter } from '../../store'
import { useState } from 'react'

export default function Filtro(){
    const filtro = useSelector(state => state.filter.filter)
    const dispatch = useDispatch()
    const [filtroInput, setFiltroInput] = useState('')

    const handleFiltrar = (e) =>{
        e.preventDefault()
        if(filtroInput.filtro !== ''){
            dispatch(setFilter(filtroInput))
            setFiltroInput({ filtro: '' });
        }else{
            alert('No te olvides de agregar el filtro!')
        }
    }

    const onInputChange = (e) =>{
        const {name, value} = e.target;
        setFiltroInput({... filtroInput, [name]: value})
    }

    console.log(filtroInput)
    return(
        <div className="Filtro">
            <input type="text" name="filtro" id="" placeholder="Filtrar por nombre" onChange={onInputChange}/>
            <button onClick={handleFiltrar}>Filtrar</button>
        </div>
    )
}
